import React, { useState } from 'react';
import AutomatonInput from './components/AutomatonInput';
import AutomatonVisualizer from './components/AutomatonVisualizer';
import './App.css'; // Ensure you're importing the App.css to apply the styles

const App = () => {
  const [automaton, setAutomaton] = useState(null);

  const handleAutomatonSubmit = (newAutomaton) => {
    setAutomaton(newAutomaton);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Automata Simulator</h1>
        <AutomatonInput onSubmit={handleAutomatonSubmit} />
        {automaton && (
          <AutomatonVisualizer automaton={automaton} />
        )}
      </header>
    </div>
  );
};

export default App;
