import React, { useEffect, useState } from 'react';
import { Stage, Layer, Circle, Arrow, Text } from 'react-konva';

const AutomatonVisualizer = ({ automaton }) => {
  const [statePositions, setStatePositions] = useState({});

  useEffect(() => {
    const positions = {};
    const radius = 200; // Radius of the circular layout
    const centerX = window.innerWidth / 2;
    const centerY = window.innerHeight / 2;
    const angleStep = (2 * Math.PI) / automaton.states.length;

    automaton.states.forEach((state, index) => {
      positions[state] = {
        x: centerX + radius * Math.cos(index * angleStep),
        y: centerY + radius * Math.sin(index * angleStep),
      };
    });

    setStatePositions(positions);
  }, [automaton.states]);

  // Helper to group and order self-loop transitions by state
  const getSelfLoopTransitions = (state) => {
    return automaton.transitions
      .filter((transition) => transition.from === state && transition.to === state)
      .map((transition) => transition.input)
      .sort(); // Sort inputs alphabetically
  };

  return (
    <Stage width={window.innerWidth} height={window.innerHeight}>
      <Layer>
        {/* Render each state as a circle */}
        {automaton.states.map((state) => {
          const { x, y } = statePositions[state] || {};
          const isAcceptState = automaton.acceptStates.includes(state);

          return (
            <React.Fragment key={state}>
              <Circle
                x={x}
                y={y}
                radius={20}
                fill="blue"
                stroke={isAcceptState ? 'green' : 'black'}
                strokeWidth={isAcceptState ? 4 : 2}
              />
              <Text x={x - 10} y={y - 5} text={state} fill="white" />
              {state === automaton.startState && (
                <Arrow
                  points={[x - 50, y, x - 20, y]}
                  fill="red"
                  stroke="red"
                  pointerLength={10}
                  pointerWidth={10}
                />
              )}
            </React.Fragment>
          );
        })}

        {/* Render transitions as arrows */}
        {automaton.transitions.map((transition, index) => {
          const { from, to, input } = transition;
          const fromPos = statePositions[from];
          const toPos = statePositions[to];

          if (!fromPos || !toPos) return null;

          const isSelfLoop = from === to;

          if (isSelfLoop) {
            // Render self-loop transitions as smooth, curved arrows
            const loopRadius = 30;
            const loopOffset = 15; // Additional offset for text to avoid overlap
            const selfLoopInputs = getSelfLoopTransitions(from).join(", ");

            return (
              <React.Fragment key={index}>
                <Arrow
                  points={[
                    fromPos.x + loopRadius, fromPos.y - loopRadius,
                    fromPos.x + loopRadius * 2, fromPos.y - loopRadius * 1.5,
                    fromPos.x + loopRadius, fromPos.y - loopRadius * 2,
                    fromPos.x, fromPos.y - loopRadius
                  ]}
                  tension={0.5} // Smooth curve for self-loop
                  stroke="black"
                  fill="black"
                  pointerLength={6}
                  pointerWidth={6}
                />
                <Text
                  x={fromPos.x + loopRadius * 1.8} // Position slightly outside loop
                  y={fromPos.y - loopRadius * 1.8 - loopOffset} // Additional offset
                  text={selfLoopInputs}
                  fontSize={14}
                  fill="black"
                />
              </React.Fragment>
            );
          } else {
            // Render straight transition arrows between different states
            return (
              <React.Fragment key={index}>
                <Arrow
                  points={[
                    fromPos.x, fromPos.y,
                    toPos.x, toPos.y
                  ]}
                  stroke="black"
                  fill="black"
                  pointerLength={10}
                  pointerWidth={10}
                />
                <Text
                  x={(fromPos.x + toPos.x) / 2}
                  y={(fromPos.y + toPos.y) / 2}
                  text={input}
                  fontSize={14}
                  fill="black"
                />
              </React.Fragment>
            );
          }
        })}
      </Layer>
    </Stage>
  );
};

export default AutomatonVisualizer;
