// frontend/src/components/AutomatonVisualizer.js
import React from 'react';
import { Stage, Layer, Circle, Line, Text } from 'react-konva';

const AutomatonVisualizer = ({ automaton }) => {
  return (
    <Stage width={window.innerWidth} height={window.innerHeight}>
      <Layer>
        {/* Render states and transitions here */}
        <Circle x={100} y={100} radius={20} fill="blue" />
        <Text x={85} y={95} text="q0" fill="white" />
      </Layer>
    </Stage>
  );
};

export default AutomatonVisualizer;
